import React, { useEffect, useState } from "react";
import { View,
        Text,
        ScrollView, 
        TextInput, 
        TouchableHighlight, 
        FlatList, 
        RefreshControl, 
        SafeAreaView} from "react-native";
import api from '../../Services/api'
import styles from "./styles";

const TabelaEstoque = () =>{
    
    const [estoque, setEstoque] = useState([]);
    const [refreshing, setRefreshing] = useState(false);
    const [topSreen, setTop] = useState(0);

    const [lista, setLista] = useState([{
        codigo:'SPMN101001',
        descricao:'FILME STRETCH MAN TRD 500 X 0,025 X 3 B4 T1,4',
        unidade:'KG',
        quantidadeEstoque:'400'

    },
    {
        codigo:'SPMN101002',
        descricao:'FILME STRETCH MAN TRD 500 X 0,025 X 3 B4 T1,4',
        unidade:'KG',
        quantidadeEstoque:'400'

    },
    {
        codigo:'SPMN101002',
        descricao:'FILME STRETCH MAN TRD 500 X 0,025 X 3 B4 T1,4',
        unidade:'KG',
        quantidadeEstoque:'400'

    },
    {
        codigo:'SPMN101002',
        descricao:'FILME STRETCH MAN TRD 500 X 0,025 X 3 B4 T1,4',
        unidade:'KG',
        quantidadeEstoque:'400'

    }
    ])

    useEffect(() =>{
        api.get('/estoque')
        .then(response =>{
            console.warn(response)
            setEstoque(response.data)
        })
        .catch(error => console.error(error))
    },[])


    return(
        <View style ={styles.container}>
            <View style = {styles.containerFilter}>
                <Text>Filtro</Text>
                <View style = {styles.wrapFilter}>
                    <View style={styles.containerInput}>
                        <TextInput ></TextInput>
                    </View>
                    <TouchableHighlight style={styles.buttonPesquisar}>
                        <Text>
                            AQUI
                        </Text>
                    </TouchableHighlight>
                    <TouchableHighlight style={styles.buttonPesquisar}>
                        <Text>
                            CAM
                        </Text>
                    </TouchableHighlight>
                </View>
            </View>
            <View style={styles.hearder}>
                <View style={styles.column}><Text style={styles.text}>Código</Text></View>
                <View style={styles.column}><Text style={styles.text}>Descrição</Text></View>
                <View style={styles.columnUnidade}><Text style={styles.text}>Uni.</Text></View>
                <View style={styles.column}><Text style={styles.text}>Quantidade</Text></View>
            </View>
            <ScrollView>

            <SafeAreaView style={{flex:1 , backgroundColor:'#4d4d4d' ,top:topSreen, flexDirection:"row"}}>
                    <FlatList data={lista}
                        renderItem={({item}) => {
                            return(
                                <View style={styles.card}>
                                <View style={styles.column}><Text style={styles.text}>{item.codigo}</Text></View>
                                <View style={styles.column}><Text style={styles.text}>{item.descricao}</Text></View>
                                <View style={styles.columnUnidade}><Text style={styles.text}>{item.unidade}</Text></View>
                                <View style={styles.column}><Text style={styles.text}>{item.quantidadeEstoque}</Text></View>
                            </View>
                                
                            )}}
                        refreshing={refreshing}
                        onRefresh={() =>{
                        
                        setRefreshing(true),
                        console.log('Teste'),
                        setRefreshing(false)
                        setTop(0)
                        }}
                    />

            </SafeAreaView>


            
            

            {/* {estoque.map(response =>{
                return(
                    <View style={styles.card}>
                        <View style={styles.column}><Text style={styles.text}>{response.codigo}</Text></View>
                        <View style={styles.column}><Text style={styles.text}>{response.descricao}</Text></View>
                        <View style={styles.columnUnidade}><Text style={styles.text}>{response.unidade}</Text></View>
                        <View style={styles.column}><Text style={styles.text}>{response.quantidadeEstoque}</Text></View>
                    </View>
                )
            })} */}

            </ScrollView>
        </View>
    )
}

export default TabelaEstoque
